import React, { useState, useEffect } from "react";
import "./Navbar.css";
import { ReactComponent as LinkedInIcon } from '../assets/linkedin.svg';
import { ReactComponent as UpworkIcon } from '../assets/upwork.svg';
import { ReactComponent as EmailIcon } from '../assets/email.svg';
import { ReactComponent as GitHubIcon } from '../assets/github.svg';

const Navbar = () => {
  const [scrollPos, setScrollPos] = useState(0);
  const [isHidden, hideNavbar] = useState(false);
  
  useEffect(() => {
    const handleScroll = () => {
      const currentScroll = window.scrollY;
      setScrollPos(currentScroll);

      if (currentScroll > 92) {
        hideNavbar(true);
      } else {
        hideNavbar(false);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <>
    <div style={{ 
      height: isHidden ? 110 : "auto", 
      transition: "height 0.3s ease" }}>
      </div>   
    <div className={`navbar ${isHidden ? "navbar-hidden" : ""}`}>
      <div className="jumplinks">
        {["About Me", "Projects", "Testimonials", "Get in Touch"].map(
          (item, index) => (
            <div key={index} className="navbar-text">
              {item}
            </div>
          )
        )}
      </div>
    <div className="contact-icons">
        <LinkedInIcon className="icon" />
        <UpworkIcon className="icon" />
        <EmailIcon className="icon" />
        <GitHubIcon className="icon" />
      </div>
    </div>
    </>
  );
};

export default Navbar;
