import React, { useState, useEffect } from "react";
import "./TestimonialCard.css"
import photo from "../assets/claymation_me.png"

const TestimonialCard = ({index}) => {

  class content{
    constructor(blurb, name, details, photo){
      this.blurb = blurb;
      this.name = name;
      this.details = details;
      this.photo = photo;    
    }
  }

  const contents = [
    new content(
      "Master Bruce puts the st in juice... JUSTICE",
      "Alfred Pennyworth",
      "Wayne Manor<br>Joke Department<br>Head Writer",
      photo
    ),
    new content(
      "Batman is my inspiration, he's why I do what I do",
      "Barbara Gordon",
      "Gotham City Police<br>Detectives Unit<br>Detective",
      photo
    ),
    new content(
      "What does Batman do when the bat signal goes up and he's like... on the toilet or something?",
      "Rob Schrab",
      "Starburns Industries<br>Cool Guy Department<br>Cool Guy",
      photo
    )
  ];

  return (
    <>
      <div className="testimonial-card">
        <p className="text">{contents[index].blurb}</p>
        <div style={{display: "flex"}}>
          <img style={{height: "85", width: "85"}}>
            {contents[index].photo}
          </img>
          <div style={{display: "flex", flexDirection: "column", flexSrink: "0"}}>
            <h1 className="title" style ={{fontSize: "24px"}}>
              {contents[index].name}</h1>
            <p className= "text" style={{fontSize: "8px"}}>
              {contents[index].details}</p>
          </div>
        </div>
      </div>
    </>
  );
};

export default TestimonialCard;

