import "./Projects.css";
import Modal from "./Modal.js"
import React, {useState} from "react";


function ProjectCard({ project, onClick }) {
  const { title, description, tags, screenshot, tpye, src } = project;

  const [selectedProject, setSelectedProject] = useState(null);

  const handleOpen = (project) => setSelectedProject(project);
  const handleClose = () => setSelectedProject(null);


  return (
    <div className="project-card">
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          height: "425px",
          justifyContent: "space-between",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            width: "800px",
          }}
        >
          <h1 className="title" style={{ color: "#000000" }}>
            {title}
          </h1>

          <div style={{ display: "flex", flexDirection: "column" }}>
            <p className="text" style={{ margin: "0px" }}>
              Made with:
            </p>
            <div className="tags-box">
              {tags?.map((tag, i) => (
                <div key={i} className="tag">
                  {tag}
                </div>
              ))}
            </div>
          </div>
        </div>

        <p className="text" style={{ width: "800px", height: "225px" }}>
          {description}
        </p>

        <div className="buttons-box">
          <button onClick={handleOpen}>LAUNCH DEMO {'>'}</button>
          <button>PROVIDE FEEDBACK {'>'}</button>
        </div>
      </div>

      <img className="screenshots" src={screenshot} alt={`${title} Screenshot`} />
    
      {selectedProject && (
        <Modal onClose={handleClose}>
          {selectedProject.type === "iframe" && (
            <iframe
              src={selectedProject.src}
              title={selectedProject.title}
              style={{
                width: "100%",
                height: "80vh",
                border: "none",
                borderRadius: "8px",
              }}
            />
          )}

          {selectedProject.type === "video" && (
            <video src={selectedProject.src} controls style={{ width: "100%" }} />
          )}

          {selectedProject.type === "images" && (
            <div className="image-gallery">
              {selectedProject.src.map((img, i) => (
                <img key={i} src={img} alt={`Slide ${i}`} />
              ))}
            </div>
          )}
        </Modal>
      )}
    
    </div>
  );
}

export default ProjectCard;
