import React from "react";
import "./Projects.css"
import "./ProjectCard.js"

const projectList = [
  {
    title: "N-Puzzle",
    description: `The N-Puzzle is a fun take on an old classic, the 8-puzzle.
      It was loosely inspired by a Princeton University Computer Science class.
      You can choose your board size anywhere from a 3-puzzle to a 99-puzzle! 
      You can undo, restart, or shuffle for a new solvable board.`,
    tags: ["JavaScript REACT", "HTML", "CSS"],
    screenshot: "../assets/tile=game-screenshot.png",
    type: "iframe",
    src: "/projects/tile-game/index.html",
  },
  // add more projects later like this:
  // { title: "...", description: "...", type: "video", src: "/videos/demo.mp4" }
];

const Projects = () => {
  
  const [selectedProject, setSelectedProject] = useState(null);

  const handleOpen = (project) => setSelectedProject(project);
  const handleClose = () => setSelectedProject(null);

  return (
    <div className="projects">
        <div className="title-box">
            <h1 className="title">Projects</h1>
            <p className="text">Games, apps and more</p>
        </div>
        <div className="project-list">
          {projectList.map((project, index) => (
            <ProjectCard
              key={index}
              title={project.title}
              description={project.description}
              onClick={() => handleOpen(project)}
            />
          ))}
        </div>
        
         {selectedProject && (
        <Modal onClose={handleClose}>
          {selectedProject.type === "iframe" && (
            <iframe
              src={selectedProject.src}
              title={selectedProject.title}
              style={{
                width: "100%",
                height: "80vh",
                border: "none",
                borderRadius: "8px",
              }}
            />
          )}

          {selectedProject.type === "video" && (
            <video src={selectedProject.src} controls style={{ width: "100%" }} />
          )}

          {selectedProject.type === "images" && (
            <div className="image-gallery">
              {selectedProject.src.map((img, i) => (
                <img key={i} src={img} alt={`Slide ${i}`} />
              ))}
            </div>
          )}
        </Modal>
      )}
    </div>
  );
}

export default Projects;
