import React from "react";
import "./TestimonialCard.css";
import photo from "../assets/claymation_me.png";

const TestimonialCard = ({ index }) => {

  const contents = [
    {
      blurb: "Master Bruce puts the st in juice... JUSTICE",
      name: "Alfred Pennyworth",
      details: "Wayne Manor/nJoke Department/nHead Writer",
      photo: photo
    },
    {
      blurb: "Batman is my inspiration, he's why I do what I do",
      name: "Barbara Gordon",
      details: "Gotham City Police/nDetectives Unit/nDetective",
      photo: photo
    },
    {
      blurb: "What does Batman do when the bat signal goes up and he's like... on the toilet or something?",
      name: "Rob Schrab",
      details: "Starburns Industries/nCool Guy Department/nCool Guy",
      photo: photo
    }
  ];

  return (
    <div className="testimonial-card">
      <p className="text">{contents[index].blurb}</p>
      <div style={{ display: "flex" }}>
        {/* Corrected the img tag */}
        <img style={{ height: "85px", width: "85px" }} src={contents[index].photo} alt="testimonial" />
        <div style={{ display: "flex", flexDirection: "column", flexShrink: "0" }}>
          <h1 className="title" style={{ fontSize: "24px" }}>
            {contents[index].name}
          </h1>
          <p className="text" style={{ fontSize: "8px" }} dangerouslySetInnerHTML={{ __html: contents[index].details }} />
        </div>
      </div>
    </div>
  );
};

export default TestimonialCard;
